INSERT INTO `kategoriler` (`id`, `kad`, `isapi`, `aciklama`) VALUES
	(1, 'Aksiyon', 'aksiyon', '&nbsp;&nbsp;Aksiyon kategorisindeki  oyunlar ile aksiyonun sonuna kadar yaşayın, bu kategorideki oyunlar ile tüm günün yorgunluğunu üstünüzden atıcaksınız\r\nen yeni aksiyon oyunlarını , aksiyon kategorisi altında bulabilirsiniz.'),
	(2, 'Macera', 'macera', '&nbsp;&nbsp;Macera mı arıyorsunuz ? Macera kategorisindeki oyunlar tam size göre çünkü macerası yüksek tüm oyunlar macera kategorisi altında sizleri beklemektedir.\r\nTek yapmanız gereken oyunu seçmen ve yeni maceralara atılmak.'),
	(3, 'Yarış', 'yaris', '&nbsp;&nbsp;Yarışçı ruhunuzu dizginlemeden geliştirmek mi istiyorsunuz yarış kategorisindeki tüm oyunlar tam sizlere göre tek yapmanız gerek bir oyun seçmek ve bir yarıştan diğer bir yarışa geçmek'),
	(4, 'Kız', 'kiz', '&nbsp;&nbsp;Kız oyunlar ile en yeni ve en zevkli kız oyunlarına ücretsiz olarak bu kategori altında ulaşabilirsiniz.\r\nAyrıca sevdiğinz kız oyunlarını listenizde toplayabilir tekrar aramakla uğraşmazsınız.'),
	(5, 'Puzzle', 'Puzzle', '&nbsp;&nbsp;Puzzle oyunları ile stresiniz atabilirsiniz ayrıca puzzle oyunları ile zevkli saatler geçirebilirsiniz.Puzzle oyunlarına ücretsiz olarak bu kategori altında ulaşabilirsiniz. En yeni  ve en güncel puzzle oyunları daima oyunde.com da'),
	(6, 'Silah', 'Silah', '&nbsp;&nbsp;Silah oyunları ile aksiyonun sonuna kadar yaşayın ,en yeni ve en güzel silah oyunları bu kategori altında ulaşabilir ayrıca oyun listemde kendi silah oyunları listenizi oluşturabilirsiniz'),
	(7, 'Beceri', 'Beceri', '&nbsp;&nbsp;Becerinizi kanıtlamak mı istiyorsunuz bu kategorideki oyunlar tam size çünkü en çok beceri isteyen oyunları bu kategoride sizleri bekliyor'),
	(8, 'Strateji', 'Strateji', '&nbsp;&nbsp;Tam bir strateji uzmanımısınız o zaman bu strateji oyunları kategorisindeki oyunlar tam size göre çünkü en yeni ve en güncel strateji oyunları bu kategori altında ulaşabilirisiniz.'),
	(9, 'İki kişilik', 'ikikisilik', '&nbsp;&nbsp;Arkadaşınızla karşılıklı oyun mu oynamak istiyorsunuz,o zaman iki kişilik oyunlar kategorisindeki oyunlar tam size göre arkadaşınızla ortak oyunlar oynayabilir ayrıca oyun listenizden iki kişilik oyun listenizi oluşturabilirsiniz.'),
	(11, 'Araba', 'araba', '&nbsp;&nbsp;Araba sürmek  en büyük zevkiniz mi ? O zaman araba oyunları kategorisideki oyunlar tam size göre çünkü bu kategorideki oyunların hepsi en yeni oyunlar ve aksiyonu bol olan oyunlardır.Araba oyunları kategorisindeki oyunlar ile hız sınırlarını zorlayın'),
	(12, 'Meb Oyunları', 'meboyunlari', '&nbsp;&nbsp;En yeni meb oyunularını bu kategoride oynayabilirsiniz.Sizin için en güncel ve en yeni meb oyunlarını bu kategoride topladık'),
	(13, '3 Boyutlu', '3Boyutlu', '&nbsp;&nbsp;3 boyutlu oyunlar ile gerçekciliği yaşayın , 2 boyutlu oyunlardan sıkıldıysanız 3 boyutlu oyunlar kategorisindeki oyunlar tam size göre hadi vakit kaybetmeden 3 boyutlu oyunlar ile eglencenin keyfine varın.'),
	(14, 'Spor', 'Spor', '&nbsp;&nbsp;Spor oyunlarına meraklıysanız spor kategorisinde ki oyunlar tam size göre spor oyunlarında çeşitli spor dallarında yapılmış olan oyunları oynayabilir arkadaşlarınıza karşı spor oyunları oynayabilirsiniz'),
	(15, 'Balon Patlatma', 'balonpatlatma', '&nbsp;&nbsp;Canınız sıkılıyorsa balon patlatma oyunları tam size göre balon patlama oyunları adında anlaşılacagı gibi balonları patlattıkça puan kazanmaktasını.Ve böyle bir balon patlama seviyesine geçmektesiniz.'),
	(16, 'Boyama', 'boyama', '&nbsp;&nbsp;Boyama yapmak yada kitap boyamak hoşunuza gidiyorsa bu kategori tam size göre bu kategoride boyama oyunlarının en yenilerine ve en güzellerine ulaşabilirsiniz.Yapmanız gereken tek şey boyama oyununu seçmek ve boyamaya başlamak'),
	(17, 'Ameliyat Oyunları', 'ameliyat', '&nbsp;&nbsp;Doktorculuk oyunları ilginizimi çekiyor o zaman bu kategorideki oyunlar tam size göre tek yapmanız gereken oyununuzu seçmek ve ameliyata başlamak');
INSERT INTO `konular` (`baslik`, `icerik`, `id`, `isapi`) VALUES
	('En yeni oyunlar', '<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Oyunde olarak sizler i&ccedil;in <a href="http://www.oyunde.com"><strong>oyun oyna</strong></a>yabileceginiz en yeni oyunları &nbsp;tek sitede topluyoruz sizlerin tek yapmanız gereken kategorilerden istediğiniz oyunu se&ccedil;mek ve eğlencenin zevkine varmak.Oyunde &uuml;zerindee toplamda 15 kategori bulunmaktadır bunlar</p>\r\n<p>\r\n	&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		Aksiyon</li>\r\n	<li>\r\n		Macera</li>\r\n	<li>\r\n		Yarış</li>\r\n	<li>\r\n		Kız</li>\r\n	<li>\r\n		Puzzle</li>\r\n	<li>\r\n		Silah</li>\r\n	<li>\r\n		Beceri</li>\r\n	<li>\r\n		Strateji</li>\r\n	<li>\r\n		İki kişilik</li>\r\n	<li>\r\n		Araba</li>\r\n	<li>\r\n		Meb Oyunları</li>\r\n	<li>\r\n		3 Boyutlu</li>\r\n	<li>\r\n		Spor</li>\r\n	<li>\r\n		Balon Patlatma</li>\r\n	<li>\r\n		Boyama</li>\r\n</ul>\r\n<p>\r\n	Ve her kategoride<a href="http://www.oyunde.com" name="Oyun oyna"><strong> oyun oyna</strong></a>bileceginiz en yeni y&uuml;zlerce oyunu sizlere sunmaktayız.</p>\r\n', 3, 'en-yeni-oyunlar'),
	('Oyun oynamanın incelikleri', '<p>\r\n	Her oyuncunun kendine &ouml;zg&uuml; bir stili vardır diye boşuna demiyorlar &ccedil;&uuml;nk&uuml; dedikleri gibi her oyuncunun kendie &ouml;zg&uuml;n <a href="http://www.oyunde.com"><strong>oyun oyna</strong></a>ma şekli vardır ve oyun oynama stili g&ouml;re oyunda y&uuml;kselirler.İşte &nbsp;bu y&uuml;zden herhangi bir oyun yada oyunları oynarken mutlaka kendinize bir yol oluşturun ve o şekilde oyun oynayın.</p>\r\n', 4, 'oyun-oynamanin-incelikleri'),
	('Oyun Oynama İpuçları', '<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Bir oyunda başarılı olmak istiyorsanız mutlaka ama mutlaka oyunua konsantre olmanız şarttır &ccedil;&uuml;nk&uuml; oyunu oyun yapan o anki konsantredir.Eğer bir b&ouml;l&uuml;me yoğunlaşırsanız o b&ouml;l&uuml;mdeki t&uuml;m engelleri takılmadan ge&ccedil;ebilirsiniz.Bu y&uuml;zden yapmanız gereken tek şey oyuna iyi konsantre olarak <a href="http://www.oyunde.com">oyun oyna</a>maktır.</p>\r\n', 5, 'oyun-oynama-ipuclari');
